import dotenv from 'dotenv';
import React from 'react';
import styled from 'styled-components';

import axios from 'axios';

import { connect } from "react-redux";
import addRemoveNotification from "../../redux/thunks/addRemoveNotification";
import {replaceWorking} from "../../redux/store";

import { NavLink, useHistory } from 'react-router-dom';

import {Div, Input, Button} from '../../styles/DefaultStyles';


import useInput from '../../tools/hooks/useInput';
import {getTimeStamp} from '../../tools/vanilla/time';
import {generatePassword} from '../../tools/vanilla/password';

import IconWorking from '../../svgs/IconWorking'

//import {ahr} from '../api';
/*
dotenv.config({ 
  path: './.env' 
});
*/

const DivCreatingPlan = styled(Div)`
  grid-area: add;
  height:100%;

  display: flex;
  flex-direction: column;
  justify-content: space-evenly;
  align-items: center;
`;


const DivHeader = styled(Div)`
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
`

const DivTitle = styled(Div)`
  font-size: 1.6rem;
`

const DivDescription = styled(Div)`
  color: ${props => props.theme.color_weak};
`


const DivBody = styled(Div)`

	display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

`



const ButtonCreatePlan = styled(Button)`
  width: 60px;
  height: 100%;
`

const DivIconWorking = styled(Div)`

`



const reqPutPlayerMmr = (battletag) => {  
  return {
    filter: {_id: battletag }
  }
};

// "listPlayerEntry._id": { $ne: battletag }  }   //very important  // https://stackoverflow.com/questions/26328891/push-value-to-array-if-key-does-not-exist-mongoose


// already know there is no PlaTeam with same id
const reqCreatePlanTeam = (idPlanTeam, battletag) => {  
  return {
    filter: {_id: idPlanTeam }

    ,update: {
      _id: idPlanTeam 
      ,listPlayerEntry: [ {_id: battletag} ]
    }
  }
};







 const CreatingPlan = ({addRemoveNotification, loading, ready, working, replaceWorking}) => {

  //{value, onChange}
  const inputPassword = useInput("");

  const history = useHistory(); 



  const onClick_ButtonCreatePlan = async (event) => {

    const idPlanTeam = getTimeStamp();
    const pwPlanTeam = generatePassword(4);  // ex: "5y7o"

    let status = {};

    try {
      replaceWorking("createPlan", true)
      await axios.post (`${process.env.REACT_APP_URL_AHR}/`, {
        _id
      });

      replaceWorking("createPlan", false)
      addRemoveNotification("success", "plan has been created!", 2000);
      status.createPlan = true; //  작업이 잘되었다고 표시

      history.push(`/team-generator/${idPlanTeam}`);
    }
    catch (error) {
      replaceWorking("createPlan", false)
      addRemoveNotification("error", "plan has not been created!", 4000);
      status.createPlan = false; //  작업이 정상적으로 끝나지 않았다고 표시 (실제로 에러가 발생하지 않는다)
    }

  }  





  return (

  <DivCreatingPlan>

    <DivHeader>

      <DivTitle> Team Generator </DivTitle>
      <DivDescription> click button to start! </DivDescription>

    </DivHeader>

    <DivBody>


      {working.createPlan ? 
      <ButtonCreatePlan> 

        <DivIconWorking>
          <IconWorking 
            width={"27px"}
            height={"24px"}
          />  
        </DivIconWorking>

      </ButtonCreatePlan> 

     : <ButtonCreatePlan onClick = {onClick_ButtonCreatePlan} > Add </ButtonCreatePlan> }



	   </DivBody>

  </DivCreatingPlan>

  )

}


