import { createStore } from "redux";

const UPDATE = "UPDATE";


const updatePlanTeam = _id => {
  return {
    type: UPDATE,
    id: _id
  };
};


const reducer = (state = { }, action) => {
  switch (action.type) {
    
    case UPDATE:
      return {
      	...state, 
      	planTeam: {} 
      };
    
    default:
      return state;
  }
};

const store = createStore(reducer);

export const actionCreators = {
  updatePlanTeam
};

export default store;
