function mapStateToProps(state) { 
  return { 
    authority: state.authority
    ,idPlanTeam: state.planTeam._id
    ,option: state.planTeam.option
    ,listPlayerEntry: state.planTeam.listPlayerEntry
    ,listResult: state.planTeam.listResult
