    test: "it is test", 
    planTeam: {}, 
    
    ready : {
      planTeam: false
    },
    
    loading : {
      planTeam: false
    }
    
  }, 
  
  
  action) => {
    
  switch (action.type) {
    
    case UPDATE:
      return {
      	...state, 
      	planTeam: action.planTeam
      };
      
    case TOGGLE_READY:
      return {
      	...state, 
      	ready: {
      	  ...ready,
      	  [action.which]: action.true_false
      	}
      };
      
    case TOGGLE_LOADING:
      return {
      	...state, 
      	loading: {
      	  ...loading,
      	  [action.which]: action.true_false
      	}
      };
      
    
    
    default:
      return state;
  }
};

const store = createStore(reducer);

export const actionCreators = {
  updatePlanTeam
};

export default store;
