        </Div>

        <Div>
          <DivRoleName> SUPPORT </DivRoleName>
          <ContainerHero> heroes </ContainerHero>
        </Div>

      </DivRoles>

    </Div>
  )

}




function mapStateToProps(state) { 
  return { 
    //dictHeroBasic: state.hots.dictHeroBasic
    //ready: state.ready 
   // ,loading: state.loading
    ///,working: state.working
  }; 
} 

function mapDispatchToProps(dispatch) { 
  return { 
    //readPlanTeam: (idPlanTeam) => dispatch(readPlanTeam(idPlanTeam)) 
    //,addRemoveNotification: (situation, message, time, idNotification) => dispatch( addRemoveNotification(situation, message, time, idNotification) )
    //,replaceWorking: (which, true_false) => dispatch(replaceWorking(which, true_false))
  }; 
}

// 컴포넌트에서 redux의 state, dispatch 를 일부분 골라서 이용가능하게 된다
export default connect(mapStateToProps, mapDispatchToProps)(AddHero);
