import defaultAxios from 'axios';

// if we not use existing axios instance, use default axios instance
const useAxios = (options, axios = defaultAxios) => {

	const [state, setState] = useState({
		loading:true,
		error: null,
		data: null
	});

	if (!options.url) {
		return;
	}

	useEffect(()=>{
		axios(options).then(data => {
			setState({
				...state,
				loading: false,
				data: data
			})
		})
	}, []);

	return state;
}
