import React from 'react';
import styled from 'styled-components';

import { NavLink } from 'react-router-dom';
import {Div} from '../styles/DefaultStyles';



const DivSub = styled(Div)`

	background-color: ${props => props.theme.COLOR_normal};

	/*background-image: url("https://www.transparenttextures.com/patterns/worn-dots.png"), linear-gradient(-10deg, hsl(210,100%,33%) 50%, hsl(210,100%,25%) 100%);*/
	/*background-color: ${props => props.theme.COLOR_normal};*/
	/*background-color: #22a7f0;*/
	/*background-image: linear-gradient(-20deg, hsl(233,60%,50%) 50%,hsl(233,60%,45%) 100%);*/
	/* hsl(233,60%,50%) */


  color: ${props => props.theme.color_strong};

  position: fixed;
  top: 0px;
  left:0px;

  display: flex;


  @media (max-width: ${props => (props.theme.media.mid_big -1) }px ) {

  	width: 100%;
  	height: 50px; 

  	flex-direction: row;

	}

	@media (min-width:  ${props => (props.theme.media.mid_big) }px) {
		width: 120px;
	  height: 100%;

		flex-direction: column;
		justify-content: flex-start;

	 }

`;


const DivTitle = styled(Div)`

	height: 160px;

	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;

	text-align: center;
	font-size: 1.3rem;
	font-family: 'Noto Sans KR', 'Noto Sans JP', sans-serif;
	font-weight: medium;

  @media (max-width: ${props => (props.theme.media.mid_big -1) }px ) {
  	display: none;
	}

	@media (min-width:  ${props => (props.theme.media.mid_big) }px) {

	}
`



const DivNavItem = styled(Div)`

	width:100%;
	height: 40px; 

	font-size: 1.1rem;

  @media (max-width: ${props => (props.theme.media.mid_big -1) }px ) {

	}

	@media (min-width:  ${props => (props.theme.media.mid_big) }px) {
	 }
`;


const activeClassName = 'nav-link-active';

const NavLinkNavItem = styled(NavLink).attrs({ activeClassName })`
  width: 90%;
  height: 100%;

	color: ${props => props.theme.color_normal};

	text-decoration: none;
	text-align: center;

	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;

	&.${activeClassName} {
		color: ${props => props.theme.color_active};
		font-weight: medium;
	}

`;

const checkActive = () => {

    return /^(\/team-generator)/.test(window.location.pathname);
}


const DivButtonToggleMode = styled(Div)`
	height: 120px;
`

const ContainerSlider = styled(Div)`
	height: 30px;
	width: 72px;

	border-radius: 15px;

	background-color: ${props => props.theme.COLOR_bg};
`

const Slider = styled(Div)`

	position: relative;


	height: 36px;
	width: 36px;

	border-radius: 50%;

	background-color: ${props => props.theme.COLOR_normal};

	transform: 'translateX(-100px)';
`
//transform: ${ props => props.theme. ? 'translateY(-100px)' : 'translateY(0)'};




// img (svg) https://www.svgrepo.com/svg/154720/hexagon
const Sub = ({match, location}) => {
	return (


  <DivSub>

  	<DivTitle>
  		PARALLEL STORM
  	</DivTitle>



  	<DivNavItem > <NavLinkNavItem to="/about"> About </NavLinkNavItem> </DivNavItem>
		<DivNavItem > <NavLinkNavItem to="/" exact={true}> Home </NavLinkNavItem> </DivNavItem>
		<DivNavItem > <NavLinkNavItem to="/team-generator" isActive={checkActive} > Team Generator </NavLinkNavItem> </DivNavItem>

		<DivButtonToggleMode>
			<ContainerSlider>
				<Slider />
			</ContainerSlider>
		</DivButtonToggleMode>

	</DivSub>

	)
}

export default Sub;

