import IconRangedAssassin from '../../svgs/roles/IconRangedAssassin'
import IconHealer from '../../svgs/roles/IconHealer'

import {CopyToClipboard} from 'react-copy-to-clipboard';

import {getRandomSubArray} from  '../../tools/vanilla/array'


const DivResult = styled(Div)`
  width: 100%;
  max-width: 600px;
  
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  
  padding-bottom: 10px;
`;

const DivTitle = styled(Div)`
  margin-top: 20px;
  font-size: 1.2rem;
  font-weight: bold;
  
  margin-bottom: 10px;
`

const DivGenerate = styled(Div)`
  margin-top: 10px;
  margin-bottom: 10px;
`

const ButtonMagic = styled(Button)`
  width: 130px;
  height: 50px;
  
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  
  border-radius: 10px;
  
  & > div {
    
    height: 100%;
  }
`

const DivAllTeams = styled(Div)`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: flex-start;
  
