  width: 320px;

	display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  overflow-y: auto;

`

const Part = styled(Div)`

  margin-top: 6px;
  margin-bottom: 6px;

  font-size: 0.9rem;

	display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

`







