import { createStore } from "redux";

const UPDATE = "UPDATE";


const updatePlanTeam = newPlanTeam => {
  return {
    type: UPDATE,
    planTeam: newPlanTeam
  };
};


const reducer = (
  
  state = { 
    
    test: "it is test", 
    planTeam: {} 
    
  }, 
  
  
  action) => {
    
  switch (action.type) {
    
    case UPDATE:
      return {
      	...state, 
      	planTeam: action.plaTeam
      };
    
    default:
      return state;
  }
};

const store = createStore(reducer);

export const actionCreators = {
  updatePlanTeam
};

export default store;
