    border-top-left-radius:    10px;
    border-top-right-radius:   10px;

  }

  &:last-child {
    border-bottom-right-radius: 10px;
    border-bottom-left-radius:  10px;

    border-bottom-style: none;

  }

*/


const DivBattletagHeader = styled(Div)`
  padding-left: 10px;

  display: block;
  text-algin: left;
`

const DivBattletag = styled(Div)`
  padding-left: 10px;
  display: block;
  text-algin: left;
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
`




const RowPlayer = ({battletag, mmr, statusPlayer}) => {

  useEffect(()=>{console.log("Each row has been rerendered")})

  const IconStatus = {
    pending: <IconPending width={"20px"} height={"20px"} />
    ,confirmed: <IconConfirmed width={"20px"} height={"20px"} />
  };

  return (

    <DivRowPlayer >

      <DivBattletag> 
        {battletag}
      </DivBattletag>

      <Div> 
        (roles)
      </Div>


      <Div> 
        {mmr}
      </Div>

      <Div> 
        {IconStatus[statusPlayer]}
      </Div>


      <Div> 
        <IconInfo
          width={"20px"}
          height={"20px"}
        /> 
      </Div>

    </DivRowPlayer>
  )
}






const Result = ({
  listPlayerEntry, option, listResult
  , addResult

}) => {





  // 1. confirmed - 2. leader - 3. less roles player(2, 1) - 4. rest
  const onClick_generateTeams = (event) => {

    const region = option.region;
    let listPlayer = (Object.keys(listPlayerEntry)).map(element=>listPlayerEntry[element]._id); // list of battletags

    let orderPlayer = listPlayer.sort( (player1, player2) => {    // mmr 높은순으로 list of battletags

      const objPlayer1 = listPlayerEntry.find(objPlayer => objPlayer._id === player1)
      const objPlayer2 = listPlayerEntry.find(objPlayer => objPlayer._id === player2)

      return (objPlayer2.mmr.standard.region - objPlayer1.mmr.standard.region);
      // ex 3333, 222, 1111
    });


    const listPlayerConfirmed = orderPlayer.filter(battletag => {
      const objPlayer = listPlayerEntry.find(objPlayer => objPlayer._id === battletag)
      return objPlayer.status === "confirmed"
    } )

    const listPlayerConfirmedLeader = listPlayerConfirmed.filter(battletag => {
      const objPlayer = listPlayerEntry.find(objPlayer => objPlayer._id === battletag)
      return objPlayer.tags.includes("leader");
    } )

    const listPlayerConfirmedNonLeader= listPlayerConfirmed.filter(battletag => {
      const objPlayer = listPlayerEntry.find(objPlayer => objPlayer._id === battletag)
      return !(objPlayer.tags.includes("leader"));
    } )


    let numberTeamsResult = option.numberTeams;    // 설정된 팀수가 잘못되었으면, 자동으로 팀수 정해주기
    if ((numberTeamsResult === 0) || listPlayerConfirmed.length < option.numberTeams * 5) {
      numberTeamsResult =  Math.floor( listPlayerConfirmed.length / 5 );
    }


    // 팀 수에 맞는 총인원 목록 정하기
    let listPlayerChoosenFinal;
    if ( listPlayerConfirmedLeader.length >= numberTeamsResult * 5 ) {  
      listPlayerChoosenFinal = getRandomSubArray(listPlayerConfirmedLeader, numberTeamsResult * 5) 
    }
    else {  
      // 리더 + 그외, 로 정해질때 그외 리스트 정하기
      const listPlayerConfirmedNonLeaderChoosen = getRandomSubArray(listPlayerConfirmedNonLeader, (numberTeamsResult * 5 - listPlayerConfirmedLeader.length ) );
      listPlayerChoosenFinal = [...listPlayerConfirmedLeader, ...listPlayerConfirmedNonLeaderChoosen];
    }



    // 총목록에서 부분 목록들로 쪼개기
    // 부분 목록 (1. 리더들, 2. 역할들 적은 사람들. 3. 나머지)

    // 1. 팀1, 팀2, ...,  팀2, 팀1  로 1. 리더들 배치, 
    // 2. 이어서 역할 수 적은 사람들 배치
    // 3. 나머지 이어서 배치


    let listIndex;
    let direction = [true, true]

    // 3 팀일때
    // [0, 0], [1,0], [2, 0], [2,1], [1, 1], [0,1], [0, 2], [1,2], ...  
    for (let i; i < numberTeamsResult * 5; i++) {

      listIndex[i]


    }


    /*
    let listTeam;
    for (var i =0; i<numberTeamsResult; i++) {
      const teamName = `team${i+1}`;
      listTeam.push(teamName);
    }
    */

    /*
    {
      _id: String,
      listPlayerBattletag: [String],
      name: String,
      group: String
    }
    */


    /*
    let team1 = {
      _id: Date.now()
      ,listPlayerBattletag: ["mbcat#1234", "mbcat#1703"]
      ,name: "dragon team"
    }

    let resultTeam = {
      added: Date.now()
      ,listTeam: [
        team1

      ]
    }

    addResult(resultTeam);


    */
  }



  return (

  <DivResult>

    <DivTitle> Result </DivTitle>

    <Div onClick={onClick_generateTeams}> 
      button
    </Div>

    {listResult[0].listTeam.map(element=>
      <Div> {element} </Div>
    )}


  </DivResult>

  )
}

/*
{(readyPlanTeam)? orderPlayerEntry[0]["_id"] : "loading..." }
    {(readyPlanTeam)? orderPlayerEntry[9]["_id"] : "loading..."}
*/

/*

    <DivTableEntry> 

    <DivRowHeader> 
      <DivBattletagHeader>  battletag </DivBattletagHeader>
      <Div> roles </Div>
      <Div> mmr </Div>
      <Div> status </Div>


      <Div> </Div>
    </DivRowHeader>

    { 
      ( listPlayerEntry ).map( (player, i) =>

        < RowPlayer 
          key={ `${player._id}_${(new Date().getTime()).toString()}` }
          battletag={player._id} 
          mmr={player.mmr.standard.NA} 
          statusPlayer={player.status} 
        /> 
      )
    }

    </DivTableEntry>

*/
//<button onClick= {refetch} > Refectch </button>



function mapStateToProps(state) { 
  return { 
    listPlayerEntry: state.planTeam.listPlayerEntry
    ,option: state.planTeam.option
    ,listResult: state.planTeam.listResult
    //listPlayerEntry: [...state.planTeam.listPlayerEntry]
    //,workingAddPlayerToListPlayerEntry: state.working.addPlayerToListPlayerEntry
    //,readyPlanTeam: state.ready.planTeam
    //,loading: state.loading
  }; 
} 

function mapDispatchToProps(dispatch) { 
  return { 
    addResult: (resultTeam) => dispatch( addResult(resultTeam) ) 
  }; 
}


// TableEntry 컴포넌트에서 redux의 state, dispatch 를 일부분 골라서 이용가능하게 된다
export default connect(mapStateToProps, mapDispatchToProps)(Result);
