  const authority = "administrator"
  // administrator, viewer

const onClick_ButtonCreatePlan = async (event) => {

    const idPlanTeam = getTimeStamp();
    const pwPlanTeam = generatePassword(4);  // ex: "5y7o"


    //let status = {};

    try {
      replaceWorking("createPlan", true);
      await axios.post (`${process.env.REACT_APP_URL_AHR}/plan-team`, {
        _id: idPlanTeam
        ,password: pwPlanTeam
      });

      replaceWorking("createPlan", false);
      addRemoveNotification("success", "new plan has been created!", 4000);
      //status.createPlan = true; //  작업이 잘되었다고 표시

      history.push(`/team-generator/${idPlanTeam}?pw=${pwPlanTeam}`);
    }
    catch (error) {
      replaceWorking("createPlan", false)
      addRemoveNotification("error", "plan has not been created!", 4000);
      //status.createPlan = false; //  작업이 정상적으로 끝나지 않았다고 표시 (실제로 에러가 발생하지 않는다)
    }

  }  



// https://ps.avantwing.com/team-generator/sss?ooo 들어가 보기
const TeamGenerator = ({match, location, loading, ready, planTeam, readPlanTeam, addRemoveNotification}) => {


  useEffect( () => { 
    if (match.path === "/team-generator/:idPlanTeam") {

      readPlanTeam(idPlanTeam);

    }

  }, []);



  const idPlanTeam = match.params.idPlanTeam;

  const query = queryString.parse(location.search);
  const pwTrying = query.pw;
  // ex: ...?pw=5837 => query = {pw: "5837"}

  console.log(match);
  console.log(location);

  //console.log(`idPlanTeam: ${idPlanTeam}`)
  if (match.path === "/team-generator") {

    return (

    <DivTeamGenerator>

      <DivA>
        <CreatingPlan /> 
      </DivA>

      <DivB>
        <SearchingPlan />
      </DivB>

    </DivTeamGenerator>
    )
  } // if


  else if (match.path === "/team-generator/:idPlanTeam") { 
   return (
   <DivTeamGenerator>

      <DivA>
        <AddingPlayer />
      </DivA>

      <DivB>
        option
      </DivB>


      <DivC>
        entry
      </DivC>


      <DivD>
        result
      </DivD>

    </DivTeamGenerator>
    )
  } // else


} //TeamGenerator



function mapStateToProps(state) { 
  return { 
    planTeam: state.planTeam
    ,ready: state.ready 
    ,loading: state.loading
  }; 
} 

function mapDispatchToProps(dispatch) { 
  return { 
    readPlanTeam: (idPlanTeam) => dispatch(readPlanTeam(idPlanTeam)) 
    ,addRemoveNotification: (situation, message, time) => dispatch( addRemoveNotification(situation, message, time) )
  }; 
}

// 컴포넌트에서 redux의 state, dispatch 를 일부분 골라서 이용가능하게 된다
export default connect(mapStateToProps, mapDispatchToProps)(TeamGenerator);
